import React from "react";
import { useRef } from "react";
import { Link, useHistory } from "react-router-dom";
// import LoginContext from "../../Context/LoginContext";
// import classes from "./SignIn.module.css";
import Form from "../../Layout/UI/Form";
import { useDispatch } from "react-redux";
import { AuthActions } from "../../Store/AuthReducer";
import Button from "../../Layout/UI/Button";

const SignIn = () => {
  const emailRef = useRef("");
  const pswdRef = useRef("");

  const history = useHistory("");
  const dispatch = useDispatch();

  // const loginCtx = useContext(LoginContext);

  const signInSubmitHandler = async (event) => {
    event.preventDefault();

    const emailValue = emailRef.current.value;
    const pswdValue = pswdRef.current.value;

    const response = await fetch(
      "https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyBkbQXz7lJKNU-7T6SEpXkPqb22k2yINFg",
      {
        method: "POST",
        body: JSON.stringify({
          email: emailValue,
          password: pswdValue,
          returnSecureToken: true,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    const data = await response.json();

    if (response.ok) {
      console.log(data.email);

      emailRef.current.value = "";
      pswdRef.current.value = "";

      // loginCtx.login(data.email, data.idToken);
      dispatch(AuthActions.login({ email: data.email, idToken: data.idToken }));

      history.replace("/welcome");
    } else {
      alert(data.error.message);
    }
  };
  return (
    <Form onSubmit={signInSubmitHandler}>
      <div>
        <h3>Sign In</h3>
      </div>
      <div>
        <input
          id="emailId"
          placeholder="Email"
          type="text"
          ref={emailRef}
        ></input>
        <input
          id="passwordId"
          placeholder="Password"
          type="password"
          ref={pswdRef}
        />
      </div>
      <Button>Sign In</Button>
      <Link to="/forgotPassword">Forgot Password?</Link>
    </Form>
  );
};
export default SignIn;
